function lgf_param = lgf_initial_param(MAX_EX_DIM,MAX_TOTAL_SPIKE_CHANS, VAR)    lgf_param = struct();    lgf_param.var = VAR * eye(MAX_EX_DIM);    lgf_param.F = diag(ones(1,MAX_EX_DIM));    lgf_param.prefdirs = zeros(MAX_EX_DIM,MAX_TOTAL_SPIKE_CHANS);    lgf_param.baseline = zeros(1,MAX_TOTAL_SPIKE_CHANS);    lgf_param.x = zeros(1,MAX_EX_DIM);    lgf_param.V = zeros(MAX_EX_DIM,MAX_EX_DIM);    lgf_param.dimension_mask = int32([1 1 zeros(1,MAX_EX_DIM-2)]);    lgf_param.activity_mask = int32(ones(1,MAX_TOTAL_SPIKE_CHANS));    lgf_param.tuning_model = int32(0);    lgf_param.direction_scale = 1.0;    lgf_param.regressor_func = @identity_regressor;